    @Test
    public void testGetTACsForAIDInTesting() {
        DipConfigHelper dipConfigHelper = new DipConfigHelper(config);
        dipConfigHelper.setupTACsForTesting();
        ArrayList<String> expected = new ArrayList<>();
        expected.add("0010000000");
        expected.add("DC4004F800");
        expected.add("DC4000A800");

        ArrayList<String> result = dipConfigHelper.getTACsForAID(AID_VISA);

        Assert.assertEquals(expected, result);
    }

    @Test
    public void testGetTACsForAIDInProduction() {
        DipConfigHelper dipConfigHelper = new DipConfigHelper(config);
        dipConfigHelper.setupTACsForProduction();
        ArrayList<String> expected = new ArrayList<>();
        expected.add("0010000000");
        expected.add("DC4004F800");
        expected.add("DC4000A800");

        ArrayList<String> result = dipConfigHelper.getTACsForAID(AID_VISA);

        Assert.assertEquals(expected, result);
    }

